import { Component, inject, OnInit } from '@angular/core';
import { DdrTableCol, DdrAction, DdrSelectItem, DdrTableItem, DdrTableComponent, DdrDropdownComponent, DdrToastService, DdrInputComponent } from 'ddr-ng';
